import React, { useState } from 'react'
import { View, Text, Image, StyleSheet, TouchableOpacity, ActivityIndicator } from 'react-native'
import * as ImagePicker from 'expo-image-picker'
import * as Analytics from 'expo-firebase-analytics'
import { Button } from 'react-native-paper';

import { morph_upload_endpoint } from '../constants/index'

export function ImageUploadButton ({
  imageRef,
  setImageRef
}) {

  const [isLoading, setIsLoading] = useState(false)
  const [isSuccess, setIsSuccess] = useState(false)
  const [isFailure, setIsFailure] = useState(false)

  const pickImage = async () => {
    await Analytics.logEvent('ButtonTapped', {
      name: 'PickImage',
      screen: 'main',
      purpose: 'Image upload button'
    })

    const result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [4, 3],
      // Quality of compression: [0, 1]
      quality: 0.1,
      base64: true
    })

    if (!result.cancelled) {
      if (imageRef === null || (imageRef instanceof Error)) {
        // image picker on iphone works different than web browser
        if (result.base64) {
          uploadImage(result.base64)
        } 
        else {
          uploadImage(result.uri)
        }
      }
    }
  }

  const uploadImage = async (img) => {
    const data = new FormData()
    data.append('firstImageRef', img)

    setIsLoading(true)
    setIsSuccess(false)
    setIsFailure(false)

    fetch(
      morph_upload_endpoint, {
        method: 'POST',
        headers: {
          Authorization: 'ImageMorpherV1'
        },
        body: data
      }
    )
      .then(res => {
        if (!res.ok) {
          throw res
        }

        return res.json()
      })
      .then(resJson => {
        Analytics.logEvent('ButtonTapped', {
          name: 'UploadSuccess',
          screen: 'main',
          purpose: 'Image upload was successful'
        })
        // On success, hide the loading spinner
        setIsLoading(false)
        setIsSuccess(true)
        setIsFailure(false)
        setImageRef(resJson)
        return resJson.data
      })
      .catch((errorResponse) => {
        errorResponse.json().then(errorMessage => {
          Analytics.logEvent('ButtonTapped', {
            name: 'UploadFailure',
            screen: 'main',
            purpose: 'Image upload failed'
          })
          console.log(errorMessage)
          setIsLoading(false)
          setIsSuccess(false)
          setIsFailure(true)
          const errorObject = new Error(errorMessage)
          setImageRef(errorObject)
        })
      })
  }

  const uploadFailureButton = <Image source={require('../../assets/error-avatar.png')} style={styles.uploadBtn} />
  const isUploadedBtn = <Image source={require('../../assets/avatar-green.png')} style={styles.uploadBtn} />
  const redoButton = <Image source={require('../../assets/redo-arrow.png')} style={styles.largeReset}></Image>
  const uploadBtn = <Button icon="face-recognition" style={styles.Btn}>Upload</Button>

  const getUploadImgBtn = () => {
    if (isLoading) {
      return (
        <ActivityIndicator size="small"/>
      )
    }
    if (isSuccess && imageRef) {
      return (
        <View>
          {isUploadedBtn}
        </View>
      )
    }

    if (isFailure) {
      return (
        <View style={styles.failedUploadImgArea}>
          <Text>{imageRef && imageRef.message}</Text>
          {imageRef && redoButton} 
        </View>
      )
    }

    else {
      return (
        <View>
          {!imageRef && uploadBtn}
          {imageRef && isUploadedBtn}
        </View>
      )
    }
  }

  return (
    <Button 
    icon="face-recognition"
    onPress={pickImage}
    style={styles.Btn}
    >
      {getImage()}
      <Text>
        {imageRef && imageRef.message}
      </Text>
    </Button>
  )

  if (isFailure) {
    return (
      <View style={styles.uploadArea} >
        <TouchableOpacity onPress={pickImage}>
          <View style={styles.failedUploadImgArea}>
            <Text>{imageRef && imageRef.message}</Text>
            {imageRef && redoButton}
            {/* {uploadFailureButton} */}
          </View>
        </TouchableOpacity>
      </View>
    )
  }

  return (
    <Button icon="face-recognition" style={styles.uploadImgBtn} onPress={pickImage} />
  )

  return (
    <View style={styles.uploadArea} >
      <Button icon="face-recognition" onPress={pickImage}>
          <View style={styles.uploadImgArea}>
            {!imageRef && uploadBtn}
            {imageRef && isUploadedBtn}
          </View>
        </Button>
    </View>
  )
}

const styles = StyleSheet.create({
  Btn: {
    width: 200,
    height: 200,
  },
  uploadImgBtn: {
    width: 300,
    height: 300,
  },
  uploadArea: {
    justifyContent: 'center',
    alignItems: 'center',
    padding: 30,
  },
  uploadImgArea: {
    alignItems: 'center',
    width: 125,
    height: 125,
    backgroundColor: '#fbfbfb',
    borderRadius: 20
  },
  uploadBtn: {
    width: '90%',
    height: '100%',
    resizeMode: 'contain'
  },
  failMessage: {
    color: 'white',
    justifyContent: 'center',
    alignItems: 'center'
  },
  failedUploadImgArea: {
    justifyContent: 'center',
    alignItems: 'center',
    width: 125,
    height: 125,
    backgroundColor: 'red',
    borderRadius: 20,
    color: 'white'
  },
  checkMark: {
    width: 30,
    height: 30,
    marginLeft: 20
  },
  largeReset: {
    width: 40,
    height: 40
  }
})
