import React, { useState } from 'react'
import { View, StyleSheet } from 'react-native'

import { MorphImageButton } from './MorphImageButton'
import { ImageUploadButton } from './ImageUploadButton'
import { MorphSequenceButton } from './MorphSequenceButton'
import { Switch } from 'react-native-paper'
import { MorphButton } from './MorphButton'

export function UploadImagesView ({firstImageRef, setFirstImageRef, secondImageRef, setSecondImageRef, morphResponse, setMorphResponse}) {

  const [isGif, setIsGif] = useState(false)

  const getMorphBtn = () => {
    if (isGif) {
      return (
        <MorphSequenceButton
        firstImageRef={firstImageRef}
        secondImageRef={secondImageRef}
        />
      )
    }
    return (
      <MorphImageButton
      firstImageRef={firstImageRef}
      secondImageRef={secondImageRef}
      morphResponse={morphResponse}

      setFirstImageRef={setFirstImageRef}
      setSecondImageRef={setSecondImageRef}
      setMorphResponse={setMorphResponse}
    />
    )
  }

  return (
    <View>
      <View style={styles.uploadBtns}>
        <View style={styles.firstBtn}>
          <ImageUploadButton
            imageRef={firstImageRef}
            setImageRef={setFirstImageRef}
          />
        </View>
        <ImageUploadButton
          imageRef={secondImageRef}
          setImageRef={setSecondImageRef}
        />
      </View>
      <View style={styles.morphBtn}>
        <MorphButton
          isGif={isGif}
          firstImageRef={firstImageRef}
          secondImageRef={secondImageRef}
          morphResponse={morphResponse}

          setFirstImageRef={setFirstImageRef}
          setSecondImageRef={setSecondImageRef}
          setMorphResponse={setMorphResponse} 
        />
        <Switch
          value={isGif}
          onValueChange={() => setIsGif(!isGif)}
        />
      </View>
    </View>
  )
}

const styles = StyleSheet.create({
  firstBtn: {
    marginRight: 60,
  },
  uploadBtns: {
    flexDirection: 'row',
    justifyContent: 'center',
  },
  morphBtn: {
    marginTop: 100,
    flexDirection: 'row',
  },
  reset: {
    width: 40,
    height: 40,
    margin: 25,
    justifyContent: 'center',
  }
})
