import React, { useState } from 'react'
import { Text, View, StyleSheet, TouchableOpacity, Image } from 'react-native'
import { LinearGradient } from 'expo-linear-gradient'
import { MorphImageButton } from './src/components/MorphImageButton'
import { MorphSequenceButton } from './src/components/MorphSequenceButton'
import { ImageUploadButton } from './src/components/ImageUploadButton'
import { useFonts } from 'expo-font'
import Home from './src/components/Home'

export default function App () {

  const [morphedImg, setMorphedImg] = useState(null);

  if (!morphedImg) {
    return (
      <Home
        morphedImg={morphedImg}
        setMorphedImg={setMorphedImg}
      />
    )
  }

  return (
    <LinearGradient
    // Background Linear Gradient
    colors={['#c2e9fb', '#a1c4fd']}
    style={styles.background}
    start={[0, 0]}
    end={[1, 1]}
    >
      <Text style={styles.title}>Face Morpher</Text>
      {
        morphedImg && <Home morphedImg={morphedImg} setMorphedImg={setMorphedImg} /> 
      }
    </LinearGradient>
  )

  // If morph image was successful, allow user to generate morph sequence
  return (
    <ViewMorphedImage
      morphedImg={morphedImg}
      setMorphedImg={setMorphedImg}
    />
  )
}

const styles = StyleSheet.create({
  background: {
    flex: 1
  },
  morphArea: {
    bottom: 50,
    justifyContent: 'center',
    alignItems: 'center',
    flex: 1
  },
  title: {
    marginTop: 30,
    fontSize: 40,
    textAlign: 'center',
    color: '#2b2b2b',
    fontWeight: 'bold',
    fontFamily: 'Roboto'
  },
  container: {
    flex: 1,
    flexDirection: 'column',
    // alignItems: 'center',
    // justifyContent: 'center'
  },
  reset: {
    width: 40,
    height: 40,
    margin: 25
  }
})
