import React from 'react'
import { View, StyleSheet, TouchableOpacity, Image } from 'react-native'
import { Button } from 'react-native-paper'
import { MorphSequenceButton } from './MorphSequenceButton'

export function MorphResponseView({firstImageRef, setFirstImageRef, secondImageRef, setSecondImageRef, morphResponse, setMorphResponse}) {

  function setInitialMorphState () {
    setFirstImageRef(null)
    setSecondImageRef(null)
    setMorphResponse(null)
  }

    return (
      <View>
        <View style={styles.viewMorphBtn}>
          <MorphButton
            firstImageRef={firstImageRef}
            secondImageRef={secondImageRef}
            morphResponse={morphResponse}

            setFirstImageRef={setFirstImageRef}
            setSecondImageRef={setSecondImageRef}
            setMorphResponse={setMorphResponse}
          />
        </View>

        <View style={styles.btnRow}>
          <Button mode="outlined" onPress={() => setInitialMorphState()}>
            Restart
          </Button>
          <MorphSequenceButton
            firstImageRef={firstImageRef}
            secondImageRef={secondImageRef}
          />
        </View>
      </View>
    )
}

const styles = StyleSheet.create({ 
  viewMorphBtn: {
    marginTop: 50,
  },
  btnRow: {
    marginTop: 40,
    flexDirection: 'row',
  },
  morphArea: {
    bottom: 50,
    justifyContent: 'center',
    alignItems: 'center',
    flex: 1
  },
  reset: {
    width: 40,
    height: 40,
    margin: 25
  }
})
