import React from 'react'
import { View, StyleSheet, TouchableOpacity, Image } from 'react-native'
import { Button } from 'react-native-paper'
import { MorphButton } from '../buttons/MorphButton'

export function MorphResponseView({firstImageRef, setFirstImageRef, secondImageRef, setSecondImageRef, morphResponse, setMorphResponse, isGif, setIsGif}) {

  function setInitialMorphState () {
    setFirstImageRef(null)
    setSecondImageRef(null)
    setMorphResponse(null)
  }

  return (
    <View>
      <View style={styles.morphBtnArea}>
        <View style={styles.morphBtn}>
          <MorphButton
            firstImageRef={firstImageRef}
            secondImageRef={secondImageRef}
            morphResponse={morphResponse}

            setFirstImageRef={setFirstImageRef}
            setSecondImageRef={setSecondImageRef}
            setMorphResponse={setMorphResponse} 
          />
        </View>
      </View>
      <View style={styles.btnRow}>
        <Button mode="outlined" onPress={() => setInitialMorphState()}>
          Restart
        </Button>
      </View>
    </View>
  )
}

const styles = StyleSheet.create({ 
  viewMorphBtn: {
    marginTop: 50,
  },
  btnRow: {
    marginTop: 40,
    flexDirection: 'row',
  },
  morphArea: {
    bottom: 50,
    justifyContent: 'center',
    alignItems: 'center',
    flex: 1
  },
  reset: {
    width: 40,
    height: 40,
    margin: 25
  }
})
