import React, { useState } from 'react'
import { Text, StyleSheet } from 'react-native'

export function InfoMessage({firstImageRef, secondImageRef}) {

  const [text, setText] = useState('Upload two images to morph');

  if (!firstImageRef && secondImageRef) {
    setText('Upload the first image')
  }
  if (firstImageRef && !secondImageRef) {
    setText('Upload the second image')
  }
  if (firstImageRef instanceof Error) {
    setText('First image is invalid. Please upload again.')
  }
  if (secondImageRef instanceof Error) {
    setText('Second image is invalid. Please upload again.')
  }

  return (
    <Text style={styles.text}>
      {text}
    </Text>
    )
}

const styles = StyleSheet.create({
  text: {
    color: 'white',
    fontSize: 20,
  }
});
