import React, { useState } from 'react'
import { Text, View, StyleSheet } from 'react-native'

// UI library - React Native Paper
import { Provider as PaperProvider } from 'react-native-paper'

import { Title } from './src/components/Title'
import { UploadImagesView }from './src/components/UploadImagesView'
import { MorphResponseView } from './src/components/MorphResponseView'

import { LinearGradient } from 'expo-linear-gradient'

export default function App () {

  const [firstImageRef, setFirstImageRef] = useState(null)
  const [secondImageRef, setSecondImageRef] = useState(null)
  const [morphImageResponse, setMorphImageResponse] = useState(null)
  const [info, setInfo] = useState('Please upload two images to morph');

  function setInitialMorphState() {
      setFirstImageRef(null)
      setSecondImageRef(null)
      setMorphImageResponse(null)
      setInfo('Please upload two images to morph');
  }

  const getView = () => {
      if (!firstImageRef && !secondImageRef) {
        setInfo('Upload two faces to morph')
      }
      if (!firstImageRef && secondImageRef) {
        setInfo('Upload the first image')
      }
      if (firstImageRef && !secondImageRef) {
        setInfo('Upload the second image')
      }
      if (firstImageRef instanceof Error) {
        setInfo('First image is invalid. Please upload again.')
      }
      if (secondImageRef instanceof Error) {
        setInfo('Second image is invalid. Please upload again.')
      }
    if (morphImageResponse) {
      return (
        <MorphResponseView
          firstImageRef={firstImageRef}
          secondImageRef={secondImageRef}
          morphImageResponse={morphImageResponse}

          setFirstImageRef={setFirstImageRef}
          setSecondImageRef={setSecondImageRef}
          setMorphImageResponse={setMorphImageResponse}
      />
      )
    }
    return (
      <UploadImagesView
        firstImageRef={firstImageRef}
        secondImageRef={secondImageRef}
        morphImageResponse={morphImageResponse}

        setFirstImageRef={setFirstImageRef}
        setSecondImageRef={setSecondImageRef}
        setMorphImageResponse={setMorphImageResponse}
        setInfo={setInfo}
      />
    )
  }

  return (
    <PaperProvider>
      <LinearGradient
        colors={['#000428', '#004e92']}
        style={styles.background}
        start={[0, 0]}
        end={[1, 1]}
      >
        <Title />
        <View style={styles.view}>
          {getView()}
        </View>
        <Text>{info}</Text>
      </LinearGradient>
    </PaperProvider>
  )
}

const styles = StyleSheet.create({
  background: {
    flex: 1,
    alignItems: 'center',
  },
  view: {
    marginTop: '20vh',
    flexDirection: 'column',
    justifyContent: 'center',
  },
})
